INSERT INTO `t_product` VALUES ('15', 'TP-LINK TL-WR845N 300M无线路由器', 'TL-WR845N采用11N无线技术，无线传输速率最高可达300Mbps，相对于传统的54M 11G和150M产品，可满足更多的无线客户端接入。在客户端数量相同时能为每个客户端提供更高的无线带宽，使局域网内的数据传输更加高效，减小数据拥塞 及网络延时，语音视频、在线点播和网络游戏更加流畅。', 'Upload/Images/product27.jpg,Upload/Images/product28.jpg', '1', '1', '2014-04-21 11:54:46', '0', '0', 'N', null, '28');
